import './App.css';
import { Container, Row, Col, Button } from 'reactstrap';
import { useState } from 'react';

const App = (props) => {
  const [count, setCount] = useState(0);
  return (
    <Container>
      <Row>
        <Col className="p-5">

          <div className="d-flex justify-content-center">
            <p>Counter</p>
            <p className="d-flex">{count}</p>
          </div>
          <div className="d-flex justify-content-center">
            <Button outline color="primary" size="lg" className="m-4 bt" onClick={() => setCount(count - 1)}>-</Button>
            <Button outline color="primary" size="lg" className="m-4 bt" onClick={() => setCount(count + 1)}>+</Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
