import './App.css';
import { Container, Row, Col, Button } from 'reactstrap';
import { useState } from 'react';

const App = (props) => {
  const {count, setCount} = useState(0);
  return (
    <Container>
      <Row>
        <Col className="p-5">
          <div>{count}</div>
          <div className="d-flex justify-content-center">
            <Button color="secondary" className="m-4 bt" onClick={() => setCount( count-1 )}>-</Button>
            <Button color="secondary" className="m-4 bt" onClick={setCount( count+1 )}>+</Button>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default App;
